mapper.select:
SELECT '시공' AS COM_SSEC, A.COM_BRAND, A.COM_AGSEC, A.ORM_NO, A.PLM_NO AS PLM_NO, CASE ( SELECT TCR.REM_CSEC FROM TC_RESMST TCR WITH (NOLOCK) WHERE TCR.PLM_NO = A.PLM_NO AND TCR.COM_SSEC = 'C18C' ) WHEN 'R' THEN '[재일정]'+A.ORM_NM ELSE A.ORM_NM END AS ORM_NM, ISNULL(( CASE WHEN ( SELECT COUNT(*) FROM TO_MCITM TM WITH (NOLOCK) WHERE TM.ORM_NO = A.ORM_NO ) > 0 THEN 'Y' ELSE '' END ), '') AS 'MICHUL_YN', ISNULL(( SELECT COUNT(DISTINCT TD.ITM_CD+TD.COL_CD) FROM TC_PLANDTL TD WITH(NOLOCK) WHERE TD.PLM_NO = A.PLM_NO AND (TD.ITM_CD NOT LIKE 'ZERO%' AND TD.ITM_CD NOT LIKE 'EXTRA%') AND TD.PLD_FQTY <> 0 AND (TD.ITM_CD NOT IN ('HW00E15EE', 'HW00E24EE', 'HW00E25EE', 'HW01E15EE', 'HW01E24EE', 'HW01E25EE', 'C37', 'X1', 'AS-1A-000')) AND TD.COM_PLDSEC IN ('C092', 'C090') ), 0) AS ITM_QTY, ISNULL(( SELECT SUM(TD.PLD_FQTY) FROM TC_PLANDTL TD WITH(NOLOCK) WHERE TD.PLM_NO = A.PLM_NO AND (TD.ITM_CD NOT LIKE 'ZERO%' AND TD.ITM_CD NOT LIKE 'EXTRA%') AND TD.PLD_FQTY <> 0 AND (TD.ITM_CD NOT IN ('HW00E15EE', 'HW00E24EE', 'HW00E25EE', 'HW01E15EE', 'HW01E24EE', 'HW01E25EE', 'C37', 'X1', 'AS-1A-000')) AND TD.COM_PLDSEC IN ('C092', 'C090') ), 0) AS ITM_SUM, ISNULL( ( CASE WHEN ( SELECT COUNT(*) FROM TW_WTRECPL WT WITH(NOLOCK) WHERE WT.ORM_NO = A.ORM_NO ) >0 THEN 'Y' ELSE '' END), '') AS RETURN_YN, ( SELECT CONVERT(INT, TCR.ARRIVAL_SEQ) FROM TC_RESMST TCR WITH (NOLOCK) WHERE TCR.PLM_NO = A.PLM_NO AND TCR.COM_SSEC = 'C18C' ) AS ARRIVAL_SEQ, A.PLM_FTM, ( SELECT TTC.CCD_NM FROM TT_COMCD TTC WITH (NOLOCK) WHERE TTC.CCD_CD = A.PLM_FTM ) AS PLM_FTMNM, ( CASE ( SELECT TCR.LOADING_YN FROM TC_RESMST TCR WITH (NOLOCK) WHERE TCR.PLM_NO = A.PLM_NO AND TCR.COM_SSEC = 'C18C' ) WHEN 'Y' THEN '상차완료' ELSE '상차전' END ) AS LOADING_YN, ( CASE WHEN ( SELECT COUNT(*) FROM TC_LOADINGISSUE TCL WITH (NOLOCK) WHERE TCL.REM_DT = ( SELECT TCR.REM_DT FROM TC_RESMST TCR WITH (NOLOCK) WHERE TCR.PLM_NO = A.PLM_NO AND TCR.COM_SSEC = 'C18C' ) AND TCL.REM_SEQ = ( SELECT TCR.REM_SEQ FROM TC_RESMST TCR WITH (NOLOCK) WHERE TCR.PLM_NO = A.PLM_NO AND TCR.COM_SSEC = 'C18C' ) AND TCL.COM_SSEC = 'C18C' ) > 0 THEN '상차이슈발생' ELSE '' END ) AS LOADINGISSUE_YN FROM TC_PLANMST A WITH (NOLOCK) WHERE A.COM_SCD = #{com_scd} AND A.STI_CD = #{sti_cd} AND A.PLM_CDT = #{date}
UNION ALL
SELECT 'AS' AS COM_SSEC, A.COM_BRAND, A.COM_AGSEC, ( SELECT TCR.ORM_NO FROM TC_RESMST TCR WITH (NOLOCK) WHERE TCR.PLM_NO = A.PLM_NO AND TCR.COM_SSEC = 'C18A' ) ORM_NO, A.PLM_NO AS PLM_NO, CASE ( SELECT TCR.REM_CSEC FROM TC_RESMST TCR WITH (NOLOCK) WHERE TCR.PLM_NO = A.PLM_NO AND TCR.COM_SSEC = 'C18A' ) WHEN 'R' THEN '[재일정]'+A.CTM_NM ELSE A.CTM_NM END AS ORM_NM, '' AS 'MICHUL_YN', ISNULL(( SELECT COUNT(DISTINCT TD.BMT_ITEM+TD.COL_CD) FROM TA_PLANDTL TD WITH(NOLOCK), TT_BOMMST TB WITH (NOLOCK) WHERE TD.PLM_NO = A.PLM_NO AND TD.PLD_FQTY <> 0 AND TD.BMT_ITEM = TB.BMT_ITEM AND TD.COL_CD = TB.COL_CD AND RIGHT(TB.BMT_NM, 5) NOT LIKE '(미상차)%' ), 0) AS ITM_QTY, ISNULL(( SELECT SUM(TD.PLD_FQTY) FROM TA_PLANDTL TD WITH(NOLOCK), TT_BOMMST TB WITH (NOLOCK) WHERE TD.PLM_NO = A.PLM_NO AND TD.PLD_FQTY <> 0 AND TD.BMT_ITEM = TB.BMT_ITEM AND TD.COL_CD = TB.COL_CD AND RIGHT(TB.BMT_NM, 5) NOT LIKE '(미상차)%' ), 0) AS ITM_SUM, '' AS RETURN_YN, ( SELECT CONVERT(INT, TCR.ARRIVAL_SEQ) FROM TC_RESMST TCR WITH (NOLOCK) WHERE TCR.PLM_NO = A.PLM_NO AND TCR.COM_SSEC = 'C18A' ) AS ARRIVAL_SEQ, A.PLM_FTM, ( SELECT TTC.CCD_NM FROM TT_COMCD TTC WITH (NOLOCK) WHERE TTC.CCD_CD = A.PLM_FTM ) AS PLM_FTMNM, ( CASE ( SELECT TCR.LOADING_YN FROM TC_RESMST TCR WITH (NOLOCK) WHERE TCR.PLM_NO = A.PLM_NO AND TCR.COM_SSEC = 'C18A' ) WHEN 'Y' THEN '상차완료' ELSE '상차전' END ) AS LOADING_YN, ( CASE WHEN ( SELECT COUNT(*) FROM TC_LOADINGISSUE TCL WITH (NOLOCK) WHERE TCL.REM_DT = ( SELECT TCR.REM_DT FROM TC_RESMST TCR WITH (NOLOCK) WHERE TCR.PLM_NO = A.PLM_NO AND TCR.COM_SSEC = 'C18A' ) AND TCL.REM_SEQ = ( SELECT TCR.REM_SEQ FROM TC_RESMST TCR WITH (NOLOCK) WHERE TCR.PLM_NO = A.PLM_NO AND TCR.COM_SSEC = 'C18A' ) AND TCL.COM_SSEC = 'C18A' ) > 0 THEN '상차이슈발생' ELSE '' END ) AS LOADINGISSUE_YN FROM TA_PLANMST A WITH (NOLOCK), TA_RPTREQ B WITH (NOLOCK) WHERE A.COM_SCD = #{com_scd} AND A.STI_CD = #{sti_cd} AND A.PLM_CDT = #{date} AND A.RPT_NO = B.RPT_NO AND A.RPT_SEQ = B.RPT_SEQ AND A.PLM_CDT = B.RPT_ENDDT AND B.STI_CD = A.STI_CD AND (SELECT COUNT(1) FROM TA_PLANDTL TP WHERE TP.PLM_NO = A.PLM_NO ) > 0 ORDER BY ARRIVAL_SEQ DESC, PLM_FTMNM DESC

mapper.update:
UPDATE TC_RESMST SET LOADING_YN = #{loading_yn}, LOADING_TIME = GETDATE()
 WHERE REM_DT = #{rem_dt}
   AND REM_SEQ = #{rem_seq}

mapper.select:


  SELECT
	ISNULL(MAX(SEQ_NO), 0) + 1
   FROM TC_LOADINGISSUE TCL WITH (NOLOCK)
  WHERE TCL.REM_DT = #{rem_dt}
    AND TCL.REM_SEQ = #{rem_seq}
    AND TCL.COM_SSEC = #{com_ssec}

mapper.update:
UPDATE TC_LOADINGISSUE
   SET LOADINGISSUE_STD = #{loadingissue_std},
       LOADINGISSUE_REMARK = #{loadingissue_remark},
       SYS_DT = GETDATE(),
       USR_CD = #{sti_cd}
 WHERE REM_DT = #{rem_dt}
   AND REM_SEQ = #{rem_seq}
   AND COM_SSEC = #{com_ssec}
   AND SEQ_NO = #{seq_no}
